// Verifica se largura de banda foi superada

function maiorquebanda(lb,x) {
    
  var soma = 0;
    for (i = 0; i < x.length; i++) {
      soma += Number(x[i]);
    }
    if (soma > lb) {
      return true;
      // } return {'soma':soma, 'false':false};
    } return { 'soma': soma };
  }

module.exports = maiorquebanda;