// Verifica se buffer atingiu limite que determina o estado CHEIO/FULL/ABOVE LIMIT (C/F/AL) ou VAZIO/EMPTY/BELLOW LIMIT (V/E/BL)

function maiorquelimite(x) {
    
    var limite = require("../psiot_sarsa_tres_buffers")
    
    if (x > limite) {
        return 1;
    } else { return 0 };
}

module.exports = maiorquelimite;